#1-Combien y a-t-il de journalistes dans ce array ?
def count_journalists
 puts "Il y a #{$journalists.length} journalistes dans la liste."
end

#2-Combien d'handle contiennent un numéro ? puts "#{n.length} handle contiennent un numéro."
def count_number
  n = 0
  $journalists.each {
    |x|
    if x[/\d+/] then n += 1 end
  }
  puts "#{n} handle contiennent un numéro."
end

#3-Combien d'handle contiennent les 4 lettres du prénom "Aude" à la suite (sans prendre en compte les majuscules) ?
def count_letters
  n = 0
  $journalists.each {
    |x|
    if x[/Aude/i] then n += 1 end
  }
  puts "#{n} handle contiennent les 4 lettres du prenom Aude à la suite."
end

#4-Combien commencent par une majuscule (première lettre juste après le @) ?
def count_maj_first
  n = 0
  $journalists.each {
    |x|
    if x[/@[A-Z]/] then n += 1 end
  }
  puts "#{n} handle commencent par une majuscule."
end

#5-Combien contiennent au moins une majuscule ?
def count_min_one_maj
  n = 0
  $journalists.each {
    |x|
    if x[/[A-Z]/] then n += 1 end
  }
  puts "#{n} handle contiennent au moins une majuscule."
end

#6-Combien y a-t-il de underscore dans tous les pseudos confondus ?
def count_underscore
  n = 0
  $journalists.each {
    |x|
    if x[/_/] then n += 1 end
  }
  puts "Il y a #{n} underscore dans tous les pseudos confondus."
end

#7-Trie la liste de handle par ordre alphabétique.
def sort_handle
  $journalists.sort_by! {
    |m|
    m[0] = ''
    m.insert(0, '- ')
    m.downcase
  }

  puts "Voici la liste de handle par ordre alphabétique :"
  puts $journalists
end

#8-Quels sont les 50 handles les plus courts de ce array ?
def fifty_shorts
  sort_by_length = $journalists.sort_by(&:length)
  smallest = []
  50.times do |i|
    smallest << sort_by_length[i]
  end

  puts "Voici les 50 handles les plus courts :"
  puts "#{smallest}"
end

#9-Quelle est la position dans l'array de la personne @epenser ?
def location_epenser
  position = 0
  $journalists.each_with_index.select {
    |m, pos|
    if m[/epenser/] then position = pos end
  }

  puts "La personne @epenser se trouve à la #{position}e position"
end

#Questions Menu
def get_question
  puts "Choisi ta question :"
  puts "1 - Combien y a-t-il de journalistes dans ce array ?"
  puts "2 - Combien d'handle contiennent un numéro ?"
  puts "3 - Combien d'handle contiennent les 4 lettres du prénom Aude à la suite ?"
  puts "4 - Combien commencent par une majuscule (première lettre juste après le @) ?"
  puts "5 - Combien contiennent au moins une majuscule ?"
  puts "6 - Combien y a-t-il de underscore _ dans tous les pseudos confondus ?"
  puts "7 - Trie la liste de handle par ordre alphabétique."
  puts "8 - Quels sont les 50 handles les plus courts de ce array ?"
  puts "9 - Quelle est la position dans l'array de la personne @epenser ?"

  print "❯ "
  question = Integer(gets.chomp)

  if question == 1
    count_journalists
  elsif question == 2
    count_number
  elsif question == 3
    count_letters
  elsif question == 4
    count_maj_first
  elsif question == 5
    count_min_one_maj
  elsif question == 6
    count_underscore
  elsif question == 7
    sort_handle
  elsif question == 8
    fifty_shorts
  elsif question == 9
    location_epenser
  else
    puts "Choisi entre 1 et 9 !"
    get_question
  end
end
get_question
